package variablesanddatatyps;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//integer
		int age=24;
		System.out.println(age);
		
		//charcter
		char gender='F';
		
		//decimal values
		double marks=75;
		float grade=7.5f;
		
		//marital status
		boolean status=true;
		
		//strings
		String name="Anuradha";
		
		//for integer values
		byte a=10;
		short b=20;
		int c= 30;
		long d= 40;
		
		
		

	}

}
